"use strict";
String.prototype.trim = function (chars = "\\s") {
    return this.rtrim(chars).ltrim(chars);
};
String.prototype.rtrim = function (chars = "\\s") {
    return this.replace(new RegExp(`[${chars}]+$`, "g"), "");
};
String.prototype.ltrim = function (chars = "\\s") {
    return this.replace(new RegExp(`^[${chars}]+$`, "g"), "");
};
